    def isIsomorphic(self, s: str, t: str) -> bool:
        # Create two mappings to store character transformations
        mapping_s = {}
        mapping_t = {}

        for char_s, char_t in zip(s, t):
            if char_s in mapping_s:
                # Check if the mapped value matches t's character
                if mapping_s[char_s] != char_t:
                    return False
            else:
                if char_t in mapping_t:
                    # Check if there's already a different mapping for t's character
                    return False

                # Create the new mappings
                mapping_s[char_s] = char_t
                mapping_t[char_t] = char_s

        return True

