def ulam_sequence(u0, u1, n):
    sequence = [u0, u1]

    while len(sequence) < n:
        sums = {}
        for i in range(len(sequence)):
            for j in range(i + 1, len(sequence)):
                s = sequence[i] + sequence[j]
                if s not in sums:
                    sums[s] = (i, j)  # Keep the indices of the pair
                else:
                    sums[s] = (i, j, sums[s])  # If the sum is already present, we append another pair to it

        # Find the smallest unique sum
        next_term = min(s for s in sums if len(sums[s]) == 1)
        sequence.append(next_term)

    return sequence[:n]

