                node.children[char] = TrieNode()
            node = node.children[char]
        node.is_end_of_word = True

    def build(root, word=''):
        if not word:
            root.is_end_of_word = True
            return
        if root.is_end_of_word:
            return
        node = root.children.get(word[0])
        if not node:
            root.children[word[0]] = TrieNode()
            node = root.children[word[0]]
        build(node, word[1:])

