        def get_neighborhood_cost(houses, cost, left, color):
            total_cost = 0
            for i in range(left, m):
                if houses[i] == 0 or houses[i] == color:
                    total_cost += cost[i][color - 1]
                else:
                    total_cost += cost[i - 1][color - 1]
                    break
            return total_cost

        # Initial house coloring
