        if target == 1:
            return sum(cost[i][color - 1] for i in range(m) if houses[i] == 0)

        def backtrack(houses, remaining_neighborhoods, current_neighborhood_count, dp):
            if current_neighborhood_count == target - 1:
                return 0

            if current_neighborhood_count >= target:
                return float('inf')

            if dp[tuple(houses)] != -1:
                return dp[tuple(houses)]

            min_cost = float('inf')
            min_color = 0

            for i in range(m):
                if houses[i] == 0:
                    for c in range(n):
                        new_houses = houses[:]
                        new_houses[i] = c + 1
                        neighborhood_cost_with_new_color = get_neighborhood_cost(houses, cost, i, c + 1)
                        new_neighborhood_count = current_neighborhood_count + (1 if (i == 0 or houses[i] != new_houses[i]) and (i == m - 1 or houses[i + 1] != new_houses[i + 1]) else 0)
                        total_cost = neighborhood_cost_with_new_color
                        if min_color > c:
                            total_cost += backtrack(new_houses, remaining_neighborhoods, new_neighborhood_count, dp)
                        else:
                            total_cost += min(backtrack(new_houses, remaining_neighborhoods, new_neighborhood_count, dp), backtrack(new_houses, remaining_neighborhoods, new_neighborhood_count + 1, dp))
                        if total_cost < min_cost:
                            min_cost = total_cost
                            min_color = c + 1
                else:
                    local_houses = houses[:]
                    if i == 0 or local_houses[i] != local_houses[i - 1]:
                        local_houses = local_houses[:i] + [0] + local_houses[i + 1:]
                        if len(local_houses) < m:
                            return -1
                        neighborhood_cost = get_neighborhood_cost(local_houses, cost, i, local_houses[i])
                        remaining_neighborhoods = remaining_neighborhoods - 1
                        remaining_neighborhoods = max(remaining_neighborhoods - 1, 0)
                        total_cost = neighborhood_cost + backtrack(local_houses, remaining_neighborhoods, current_neighborhood_count, dp)
                        if total_cost < min_cost:
                            min_cost = total_cost

            dp[tuple(houses)] = min_cost
            return min_cost

        dp = {}
        initial_neighborhoods -= 1
        if initial_neighborhoods < target - 1:
            return -1
        return backtrack(houses, initial_neighborhoods, 0, dp)

