from itertools import permutations

def get_sequence(offset, size):
    if offset > max_permutations(9) or size <= 0:
        return []

    offset = int(offset)
    pandigitals = []

    # Generate all permutations of digits 0-9
    for perm in permutations(range(10), 10):
        num = int(''.join(map(str, perm)))
        if num >= offset:
            pandigitals.append(num)
            if len(pandigitals) == size:
                return sorted(pandigitals)

    return sorted(pandigitals)

def max_permutations(digits):
    if len(digits) == 0:
        return 1
    return max_permutations(digits[1:]) * digits[0]

