def diamond(n):
    if n <= 0 or n % 2 == 0:
        return None
    diamond = []
    n = (n + 1) // 2
    for i in range(n):
        if i == 0 or i == n - 1:
            diamond.append([" " * (n - i - 1) + "*"] * (i + 1))  # Upper part
        else:
            diamond.append([" " * (n - i - 1) + "*"] * (i + 1) + [" " * (2 * i) + "*" * (n - i - 1)] + [" " * (n - i - 1) + "*"] * (i + 1))  # Upper part
    for i in range(n, 2 * n - 1, -1):
        if i == 2 * n - 2 or i == 2 * n - 1:
            diamond.append(["- " * (n - (2 * n - 1) // 2) + "*"] * ((2 * n - 1) // 2))  # Lower part
        else:
            diamond.append([" " * n] * ((2 * n - 1) // 2))  # Spacing
            diamond.append([" - " * (-(n - i) % (2 * n - 1) // 2) - "*"] * (n - i - 1))  # Lower part

    result = "\n".join("".join(line) for line in diamond)
    return result

