def calculate_grundy(n, memo):
    if n == 0:
        return 0
    if n in memo:
        return memo[n]

    reachable = set()
    for divisor in [2, 3, 4, 5, 6]:
        if n % divisor == 0:
            reachable.add(calculate_grundy(n // divisor, memo))

    mex = 0
    while mex in reachable:
        mex += 1

    memo[n] = mex
    return mex

def turn_to_play(grundys):
    xor_result = 0
    for g in grundys:
        xor_result ^= g
    return xor_result

