    def minCost(self, n: int, cuts: List[int]) -> int:
        cuts.sort()
        dp = [n] * (len(cuts) + 1)
        cuts = [n] + cuts[::-1]

        for cut in cuts:
            cumulative_sum = 0
            for j in range(len(cuts)):
