def is_coexistential(s, m, n, t):
    if m == n: return True
    if not t: return True
    i, j, k = 0, 0, 0

    t = t.replace(" ", "")

    while i < len(s) and j < len(t) and k < len(t):
        if not s[i] or not t[j]:
            i += 1
            j += 1
            k += 1
        elif s[i] == t[j]:
            i += 1
            j += 1
            k += 1
        else:
            j, k = j + 1, k
    return i == len(s) and j == len(t) and k == len(t)

def dfs(s, s_i, a, m, n, q, t, ans):
    if s_i == len(a):
        if is_coexistential(s, m, n, t):
            ans[0] = True
        return
    for c in a:
        s_i[0] += c
        dfs(s, s_i, a, m, n, q, t, ans)
        s_i[0] -= c

