def align_right(text, width):
    words = text.split()
    result = []
    line = ""

    for word in words:
        if len(line) + len(word) + (1 if line else 0) <= width:
            line += (" " if line else "") + word
        else:
            result.append(line.ljust(width, ' '))
            line = word

    result.append(line.ljust(width, ' '))

    # Remove the trailing spaces in result list and join them with new line
    return "\n".join([line.rstrip() for line in result])
