from typing import List

class Solution:
    def findAndReplacePattern(self, words: List[str], pattern: str) -> List[str]:
        def can_form_permutation(word, pattern):
            mapping_word_to_pattern = {}
            mapping_pattern_to_word = {}

            for w, p in zip(word, pattern):
                if w in mapping_pattern_to_word:
                    if mapping_pattern_to_word[w] != p:
                        return False
                else:
                    mapping_pattern_to_word[w] = p

                if p in mapping_word_to_pattern:
                    if mapping_word_to_pattern[p] != w:
                        return False
                else:
                    mapping_word_to_pattern[p] = w

            return True

        result = []
        for word in words:
            if can_form_permutation(word, pattern):
                result.append(word)

        return result
