Here’s the complete JavaScript solution:

```javascript
const BigNumber = require('bignumber.js');

function fib_nth(n) {
    const F = [
        [1, 1],
        [1, 0]
    ];
    let res = [[1, 0], [0, 1]];

    power(res, F, n - 1);
    return res[0][0].toString();  // Convert to string to get digit by digit for counting
}

function matrixMult(A, B) {
    return [
        [A[0][0].times(B[0][0]).plus(A[0][1].times(B[1][0])), A[0][0].times(B[0][1]).plus(A[0][1].times(B[1][1]))],
        [A[1][0].times(B[0][0]).plus(A[1][1].times(B[1][0])), A[1][0].times(B[0][1]).plus(A[1][1].times(B[1][1]))]
    ];
}

function matrixPow(matrix, n) {
    let result = [[BigNumber(1), BigNumber(0)], [BigNumber(0), BigNumber(1)]];
    while (n > 0) {
        if (n % 2 === 1) {
            result = matrixMult(result, matrix);
        }
        matrix = matrixMult(matrix, matrix);
        n = Math.floor(n / 2);
    }
    return result;
}

function power(matrix, base, n) {
    let result = matrixPow(base, n);
    matrix[0][0] = result[0][0];
    matrix[0][1] = result[0][1];
    matrix[1][0] = result[1][0];
    matrix[1][1] = result[1][1];
}

function countOccurrences(digits) {
    let count = new Map();
    digits.split('').forEach(digit => {
        count.set(digit, (count.get(digit) || 0) + 1);
    });
    return [...count].sort(([, value1], [, value2]) => value2 - value1);
}

function fib_digits(n) {
    let fibStr = fib_nth(n);
    let digits = fibStr.length === 19 ? fibStr.slice(7): fibStr.slice(0, 19);  // Only consider up to 19 digits after 10000
    let counts = countOccurrences(digits);
    return counts.map(([digit, count]) => [Number(digit), count]);
}

