```scala
object ArithmeticExpression {
  sealed trait Expr
  case class LitInt(value: Int) extends Expr
  case object Add extends Expr
  case object Sub extends Expr
  case object Mul extends Expr
  case object Div extends Expr
  case class Var(name: Char) extends Expr

  object Parser {
    def apply(exp: String): Expr = parseExpression(exp)

    private def parseExpression(exp: String): Expr = {
      // Simplistic parser that doesn't handle all edge cases but should work for the requirements
      // This could be improved to handle more complex expressions and ensure complete parsing.

      var index = 0
      var buffer = exp
      buffer = buffer.replace(" ", "")

      def nextChar = if (index < buffer.length) buffer.charAt(index) else '\0'

      val parseOperation = (op: () => Unit) =>
      while ({ val ch = nextChar; ch != '\0' && ch != '+' && ch != '-' && ch != '*' && ch != '/'; op(); index += if (index < buffer.length && ch != '\0') 1 else 0 }) { }

      val parseInt = parseExpressionFromInt => {
        val start = index
        while (index < buffer.length && (Character.isDigit(nextChar) || nextChar == 'x')) index += 1
        parseExpressionFromInt(index - start.toInt)
      }

      def parseExpressionFromInt(startIndex: Int) =
        if (startIndex >= buffer.length) throw new IllegalArgumentException("Invalid expression format")
        val value = parseInt(_.toString).toInt
        LitInt(value)

      val parseVariable = parseExpressionFromVar => {
        val start = index
        while (index < buffer.length && nextChar >= '0' && nextChar <= '9') index += 1
        val varName = buffer.chars().collect {
          case x: Char if x >= '0' && x <= '9' => x
        }
        parseExpressionFromVar(start, varName)
      }

      def parseExpressionFromVar(startIndex: Int, varName: Int) =
        if (startIndex != index) Var(varName)

      val opChars = Array('+', '-', '*', '/')
      if (index == buffer.length) throw new IllegalArgumentException("Invalid expression format")

      val isOperator = (ch: Int) => opChars.contains(ch)
      val left = (op: () => Unit) => parseOperation(op).asInstanceOf[Expr]
      val operationIndex = index
      val nextOp = nextChar

      if (!isOperator(nextOp)) List(Var(nextOp - '0').asInstanceOf[Any])

      val right = (op: () => Unit, opName: Int) =>
        (op()) match {
          case a: Add         => LitInt(a)
          case b: Sub         => LitInt(b)
          case c: Mul         => LitInt(c)
          case d: Div         => LitInt(d)
          case e: LitInt      => e
          case f: Var         => if (isOperator(nextOp)) f.asInstanceOf[LitInt] else f.asInstanceOf[Add]
        }

      if (index == buffer.length - opName.length) List(opName) else opName
    }

    def evaluate(expression: Expr, args: Array[Int]): Int = expression match {
      case LitInt(value) => value
      case Var(name) => args(name - '0') // Map variable to its index+1 because 0-based index
      case Add        => evaluate(left, args) + evaluate(right, args)
      case Sub        => evaluate(left, args) - evaluate(right, args)
      case Mul        => evaluate(left, args) * evaluate(right, args)
      case Div        => evaluate(left, args) / evaluate(right, args)
    }
  }

  def eval(expression: String, args: Array[Int]): Int = {
    val parsedExp = Parser(expression)
    Parser.evaluate(parsedExp, args)
  }
}

