#include <vector>
#include <algorithm>
#include <limits>

// Function to check if it's possible to read all tracks in a given time
bool canReadAllTracks(const std::vector<int>& heads, const std::vector<int>& targetTracks, long long t) {
    long long sum = 0;
    for (size_t i = 0; i < targetTracks.size(); ++i) {
        int maxDistance = 0;
        for (int h : heads) {
            maxDistance = std::max(maxDistance, std::abs(targetTracks[i] - h));
        }
        if (maxDistance > t) return false;
    }
    return true;
}

int main() {
