    long long n, m;
    std::cin >> n >> m;

    std::vector<long long> heads(n), targetTracks(m);
    for (int i = 0; i < n; i++) {
        std::cin >> heads[i];
    }
    for (int i = 0; i < m; i++) {
        std::cin >> targetTracks[i];
    }

    // Sort initial positions
    std::sort(heads.begin(), heads.end());
    // Sort target tracks
    std::sort(targetTracks.begin(), targetTracks.end());

    // Binary search to find the minimum time required
    long long left = 0, right = *std::max_element(targetTracks.begin(), targetTracks.end()) - *std::min_element(targetTracks.begin(), targetTracks.end());
    long long result = right;
    while (left <= right) {
        long long mid = left + (right - left) / 2;
        if (canReadAllTracks(heads, targetTracks, mid)) {
            right = mid - 1;
            result = mid;
        } else {
            left = mid + 1;
        }
    }

    // Output the minimum time required
    std::cout << result << std::endl;

    return 0;
}
```

