class Solution:
    def braceExpansionII(self, expression: str) -> List[str]:
        from collections import deque

        def get_words(expr):
            if expr not in word_set:
                # Base case: single character
                if len(expr) == 1:
                    return [expr]
                # Recursive case: handling nested and concatenated expressions
                curr_word = []
                stack = deque()
                i = 0
                while i < len(expr):
                    if expr[i] == '{':
                        stack.append(i)
                    elif expr[i] == '}':
                        start = stack.pop()
                        end = i
                        curr_word.append(expr[start + 1:end])
                    elif expr[i] == ',' and not stack:
                        # Split a standalone union
                        stack.append(i)
                    else:
                        curr_word.append(expr[i])
                    i += 1
                # Recursively get words for the inner sets
                words = []
                i = 0
                while i < len(curr_word):
                    if '(' in curr_word[i] and ')' in curr_word[i+1]:
                        words.extend(words_for_expr(curr_word[i][1:-1]))
                    else:
                        words.extend(words_for_expr(curr_word[i]))
                    i += 1
                return words

        def braceExpansion(expression: str):
            current = []
            stack = deque()
            results = []
            for char in expression:
                if char == '{':
                    stack.append(char)
                elif char == '}':
                    stack.pop()
                    if not stack:
                        current.append(results.pop())
                else:
                    if not stack:
                        current.append(char)
                    else:
                        if stack:
                            results.append([current.pop() + c for c in braceExpansion(current.pop() + str(char))])
                if not stack:
                    results.append(list(set(current)))
            return results

        # We use a dictionary to avoid recalculating the result for the same expression
        word_set = {}

        def words_for_expr(expr):
            if expr in word_set:
                return word_set[expr]

            words = get_words(expr)
            result = braceExpansion(expr)
            if result:
                final_words = list(set([''.join(p) for p in result[0]]))
                for next_expr in result:
                    final_words.extend([''.join(p) for p in braceExpansion(expr)])
            else:
                final_words = words

            word_set[expr] = sorted(final_words)
            return word_set[expr]

        return words_for_expr(expression)

