class Solution:
    def braceExpansionII(self, expression: str) -> List[str]:
        def parse(expression):
            tokens = []
            stack = []
            temp = ""
            for char in expression:
                if char.isalpha():
                    temp += char
                elif char == '{':
                    stack.append('{')
                    stack.append(temp)
                    temp = ""
                elif char == '}':
                    token = stack.pop()
                    while stack[-1] != '{':
                        token= stack.pop() + token
                    stack.pop()
                    stack.append(temp)
                    stack.append(token)
                    temp = ""
                elif char == ',':
                    tokens.append(''.join(stack))
                    stack = []
            return tokens

        def expand(tokens):
            result = []
            length = len(tokens)
            if length == 1:
                return [tokens[0]]
            word1 = parse(tokens[0])
            for i in range(length - 1):
                word2 = parse(tokens[i + 1])
                temp_result = []
                for word in word1:
                    for other in word2:
                        temp_result.append(word + other)
                if i != length - 2:
                    word1 = parse(temp_result)
            return expand(tokens[0]) + expand(['', '', temp_result])

        res = parse(expression)
        return sorted(expand(res))
