```python
def ds_multof_pfs(n_min, n_max):
    def prime_factorization(n):
        factors = []
        # Check for number of 2s that divide n
        while n % 2 == 0:
            factors.append(2)
            n = n // 2
        # n must be odd at this point, so a skip of 2 (i.e. i = i + 2) can be used
        for i in range(3, int(n**0.5) + 1, 2):
            while n % i == 0:
                factors.append(i)
                n = n // i
        # Condition if n is a prime number greater than 2
        if n > 2:
            factors.append(n)
        return factors

    def sum_of_divisors(n):
        divisors = [1]
        for i in range(2, int(n**0.5) + 1):
            if n % i == 0:
                divisors.append(i)
                if i != n // i:
                    divisors.append(n // i)
        return sum(divisors) + n

    def sum_of_prime_factors(factors):
        return sum(factors)

    def is_divisible(n):
        pfs = sum(prime_factorization(n))
        ds = sum_of_divisors(n)
        return ds % pfs == 0

    result = []
    for number in range(n_min, n_max + 1):
        if is_divisible(number):
            result.append(number)
    return sorted(result)

