class Solution:
    def isValid(self, code: str) -> bool:
        stack = []
        i = 0
        while i < len(code):
            if code[i] == '<':
                if code[i+1] == '!':
                    i = self.parseCDATA(code, i)
                    continue
                end_i = self.findEndTag(code, i)
                # Extract the tag name and tag content
                if end_i == -1:
                    return False
                tag_content = code[i+2:end_i-1]
                i = end_i  # Move past the current tag
                tag_name_start = i - 1

                if tag_content:
                    is_start = True if code[i] != '/' else False
                    tag_name = code[tag_name_start + (1 if is_start else 2): i - 1]
                    valid = self.validateTagName(tag_name)

                    if valid:
                        if not stack or (is_start and stack[-1] != tag_name):
                            stack.append(tag_name)
                        elif (not is_start and stack[-1] != tag_name) and (not stack[-1].lower == tag_name.lower() or not tag_name.lower()):
                            return False
                    elif tag_name == 'CDATA']:
                        stack.append('CDATA')
                    else:
                        return False
                i = end_i
            else:
                i += 1

        return len(stack) == 0

    def parseCDATA(self, code, start) -> int:
        stack = []
        end = start + len(code)
        while start < end:
            if code[start] == ']':
                if not stack:
                    return start + 3
                stack.pop()
            elif code[start] == '[':
                stack.append('[')
            start += 1
        return end

    def findEndTag(self, code, start) -> int:
        stack = []
        end = start + len(code)
        i = start + 1
        while i < end:
            if code[i] == '<':
                if code[i+1] == '!' and code[i+1] == code[i+2]:
                    return i + self.parseCDATA(code, i)
                tag_end = self.findEndTagCode(code, i)
                if tag_end != -1:
                    return tag_end + 2  # Move past the full end tag
            i += 1
        return -1

    def findEndTagCode(self, code, start):
        stack = []
        i = start + 1
        while i < len(code):
            if code[i] == '<':
                if code[i+1] == '!':
                    i = self.parseCDATA(code, i)
                    continue
                end = self.findEndTag(code, i)
                if end:
                    tag_content = code[i+2:end-1]
                    if not stack or len(stack) == 0:
                        stack.append(i)
                    i = end
                    if self.validateCDATA(stack, i):
                        stack.pop()
                    else:
                        return -1
            elif code[i] == '!' and not stack[-1]:
                if code[i-3:i+3] == "<![CDATA[" and stack:
                    stack.append("CDATA")
                    start = i + 9
            i += 1
        if not stack:
            return len(code)
        return -1

    def validateTagName(self, tag_name) -> bool:
        if not tag_name or len(tag_name) < 1 or len(tag_name) > 9:
            return False
        for ch in tag_name:
            if not ch.isupper():
                return False
        return True
```

