                1: "Eleven",
                2: "Twelve",
                3: "Thirteen",
                4: "Fourteen",
                5: "Fifteen",
                6: "Sixteen",
                7: "Seventeen",
                8: "Eighteen",
                9: "Nineteen"
            }
            return switcher.get(num, "")

        def two(num):
            switcher = {
                2: "Twenty",
                3: "Thirty",
                4: "Forty",
                5: "Fifty",
                6: "Sixty",
                7: "Seventy",
                8: "Eighty",
                9: "Ninety"
            }
            return switcher.get(num//10, "")

        def ten(num):
            switcher = {
                2: "Twenty",
                3: "Thirty",
                4: "Forty",
                5: "Fifty",
                6: "Sixty",
                7: "Seventy",
                8: "Eighty",
                9: "Ninety"
            }
            if 2 <= num // 10 <= 9:
                return switcher.get(num // 10, "")
            else:
                return switcher.get(num//10 * 10, "")

        def three(num):
            if num == 0:
                return ""
            if 100 <= num <= 999:
                hundreds = num // 100
                remainder = num - (hundreds * 100)
                if remainder != 0:
                    return one(hundreds) + " Hundred " + two_less_remainder(num - (hundreds * 100))
                else:
                    return one(hundreds) + " Hundred"
            return ""

        def four(num):
            if num == 0:
                return ""
            if 1000 <= num <= 999999:
                thousands = num // 1000
                remainder = num % 1000
                return three(thousands) + " Thousand" + two_less_remainder(remainder)
            return ""

        def five(num):
            if num == 0:
                return ""
            if 1000000 <= num <= 999999999:
                millions = num // 1000000
                remainder = num % 1000000
                return three(millions) + " Million" + four(remainder)
            return ""

        def six(num):
            if num == 0:
                return ""
            if 1000000000 <= num <= 999999999999:
                billions = num // 1000000000
                remainder = num % 1000000000
                return three(billions) + " Billion" + five(remainder)
            return ""

        return six(num)
