import math

class Vector:
    def __init__(self, *args):
        if len(args) == 3:
            self.x = args[0]
            self.y = args[1]
            self.z = args[2]
        else:
            raise ValueError("Vector must be initialized with 3 components")

    def __add__(self, other):
        return Vector(self.x + other.x, self.y + other.y, self.z + other.z)

    def __sub__(self, other):
        return Vector(self.x - other.x, self.y - other.y, self.z - other.z)

    def __eq__(self, other):
        return self.x == other.x and self.y == other.y and self.z == other.z and \
               math.isclose(self.magnitude, other.magnitude) and self.direction_vectors(other)

    def direction_vectors(self, other):
        return (self.x/self.magnitude == other.x/other.magnitude and
                self.y/self.magnitude == other.y/other.magnitude and
                self.z/self.magnitude == other.z/other.magnitude)

    def cross(self, other):
        return Vector(self.y * other.z - self.z * other.y,
                      self.z * other.x - self.x * other.z,
                      self.x * other.y - self.y * other.x)

    def dot(self, other):
        return self.x * other.x + self.y * other.y + self.z * other.z

    def to_tuple(self):
        return (self.x, self.y, self.z)

    def __str__(self):
        return f"Vector({self.x}, {self.y}, {self.z})"

    @property
    def magnitude(self):
        return math.sqrt(self.x**2 + self.y**2 + self.z**2)

    def x(self):
        return self.x

    def y(self):
        return self.y

    def z(self):
        return self.z

