```python
def whoIsWinner(moves, connect, size):
    from collections import defaultdict

    def add_to_board(board, col, player):
        # Add piece to board in the respective column
        for row in range(size - 1, -1, -1):
            if board[row][col]:
                continue
            board[row][col] = player
            return row
        return None

    def check_win(board, col, row, dcol, drow, player, dir):
        # Check in given direction
        for i in range(1, connect):
            cr, cc = row + i * drow, col + i * dcol
            if 0 <= cr < size and 0 <= cc < size and board[cr][cc] == player:
                continue
            else:
                break
        else:
            return True
        return False

    def check_vertical_win(board, player, col):
        return any([board[row][col] == player for row in range(size - connect + 1, size)])

    def check_horizontal_win(board, player, col, row):
        for i in range(1, connect):
            if board[row][col + i] == player and board[row][col + i] == 4 or board[row][col + i] == 3:
                return True
        return False

    def check_diagonal_win(board, player, col, row):
        dcol = -1
        drow = -1
        if col > 0 and row > 0:  # Upper leading diagonal
            for i in range(1, connect):
                dcol = -1
                drow = -1 if i == 0 else 1 * (2**i - 1)
                if 0 <= row + drow < size and 0 <= col + dcol < size and board[row + drow][col + dcol] == player:
                    continue
                else:
                    return False
        else:
            return False

        # Diagonal down right
        drow = -1
        dcol = 1
        for i in range(1, connect):
            if 0 <= row - drow < size and 0 <= col + dcol < size and board[row-drow][col + dcol] == player:
                continue
            else:
                return False

        # Diagonal up right
        drow = 1
        dcol = 1
        for i in range(1, connect):
            if 0 <= row + drow < size and 0 <= col - dcol < size and board[row + drow][col - dcol] == player:
                continue
            else:
                return False

        return False

    board = defaultdict(int)
    for move in moves:
        col = ord(move[0]) - ord('A')
