public class DecomposeNumber
{
    public static List<object>[] decompose(int num)
    {
        List<object>[] result = new List<object>[2];

        if (num == 0)
        {
            result[0] = new List<object>();
            result[1] = 0;
            return result;
        }

        int remainder = num;
        List<int> kList = new List<int>();

        for (int baseVal = 2; baseVal <= remainder; baseVal++)
        {
            int power = 1;
            while (remainder > 0 && power < baseVal)
            {
                if (remainder % (power + 1) == 0)
                {
                    kList.Add(power + 1);
                    remainder /= (power + 1);
                }
                power++;
            }
        }

        if (remainder != 0)
        {
            kList.Add(remainder);
        }

        result[0] = kList;
        result[1] = num - Decompose(Convert.ToInt32(result[0]), baseVal, 0, 0);

        return result;
    }

    public static int Decompose(List<int> kList, int baseVal, int currentIndex, int prevPower)
    {
        if (currentIndex >= kList.Count)
        {
            return 0;
        }

        int currentVal = kList[currentIndex];
        int newPower = 1;

        for (int i = 0; i < currentVal; i++)
        {
            newPower *= baseVal;
        }

        if (currentIndex == 0 || currentVal - 1 != 0)
