            int baseSum = Decompose(kList, baseVal - 1, currentIndex + 1, newPower);
            currentVal = kList[currentIndex];
            newPower = 1;
            for (int i = 0; i < currentVal - 1; i++)
            {
                newPower *= baseVal;
            }

            return baseSum + newPower;
        }
    }

