```python
def isTree(matrix):
    if not matrix:
        # An empty graph is considered a tree
        return True

    num_nodes = len(matrix)

    # Check if the graph is an empty graph, i.e., with no edges
    if all(not connections for connections in matrix):
        return True

    # Check if each node has exactly two parents
    num_parents = [0] * num_nodes

    for connections in matrix:
        for node in connections:
            if 0 <= node <= num_nodes:
                num_parents[node] += 1

    # Count the number of nodes with more than two parents (which is not possible in a tree)
    no_duplicates = 0

    for count in num_parents:
        if count > 2:
            return False
        elif count < 2:
            no_duplicates += 1

    # Check if the graph is connected
    visited = [False] * num_nodes

    def isConnected(node):
        if visited[node]:
            return False

        visited[node] = True

        for neighbor in matrix[node]:
            if not visited[neighbor]:
                if not isConnected(neighbor):
                    return False

        return True

    # Check if all nodes are reachable
    for i in range(num_nodes):
        if not visited[i] and not isConnected(i):
            return False

    return no_duplicates == 1
