class Solution:
    def integerReplacement(self, n: int) -> int:
        def count_steps_to_one(n: int) -> int:
            if n == 1:
                return 0
            if n % 2 == 0:
                return 1 + count_steps_to_one(n // 2)
            else:
                return 1 + min(
                    count_steps_to_one(n - 1),
                    count_steps_to_one(n + 1)
                )

