from typing import List

class Solution:
    def minCost(self, houses: List[int], cost: List[List[int]], m: int, n: int, target: int) -> int:
        max_c = float('inf')

        def backtrack(i, neighborhoods, cost_):
            nonlocal max_c
            if i == m:
                if neighborhoods == target:
                    max_c = min(max_c, cost_)
                return

            if houses[i] > 0:
                backtrack(i + 1, neighborhoods, cost_)
            else:
                for j in range(1, n + 1):
                    if j != houses[i - 1]:
                        continue
                    if neighborhoods == target:
                        backtrack(i + 1, neighborhoods, cost_ + cost[i][j - 1])
                    else:
                        backtrack(i + 1, neighborhoods + 1, cost_ + cost[i][j - 1])

        backtrack(0, 0, 0)
        return max_c if max_c != float('inf') else -1
