```python
from typing import List

class Solution:
    def pourWater(self, heights: List[int], V: int, K: int) -> List[int]:
        def canFlowLeft(i: int):
            return i > 0 and heights[i] > heights[i - 1]

        def canFlowRight(i: int):
            return i < len(heights) - 1 and heights[i] > heights[i + 1]

        changed = True
        while V > 0 and changed:
            changed = False
            # Try to make the water flow left or right
            if V > 1 and canFlowLeft(K):
                while K > 0 and canFlowLeft(K):
                    K -= 1
                    if heights[K] == heights[K + 1]:
                        heights[K] += 1
                        heights[K + 1] += 1
                    elif heights[K] < heights[K + 1]:
                        heights[K] += 1
                    elif heights[K] > heights[K + 1]:
                        heights[K + 1] += 1
                    else:
                        if K > 0:
                            heights[K - 1] += 1
                            heights[K] += 1
                        else:
                            heights[K] += 1
                    if heights[K] == heights[K + 1] or (K > 0 and not canFlowLeft(K)):
                        heights[K] += 1
                        changed = True
            elif canFlowRight(K):
                while K >= 0 and canFlowRight(K):
                    K += 1
                    if heights[K] == heights[K - 1]:
                        heights[K] += 1
                        heights[K - 1] += 1
                    elif heights[K] < heights[K - 1]:
                        heights[K] += 1
                    elif heights[K] > heights[K - 1]:
                        heights[K - 1] += 1
                    else:
                        if K < len(heights) - 1:
                            heights[K + 1] += 1
                            heights[K] += 1
                        else:
                            heights[K] += 1
                    if heights[K] == heights[K - 1] or (K < len(heights) - 1 and not canFlowRight(K)):
                        heights[K] += 1
                        changed = True
            # If water doesn't flow left or right, then it stays in the current position
            else:
                heights[K] += 1
                changed = True

            V -= 1

        return heights

# Example usage:
