class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.blocked = [False] * n
        self.rank = [1] * n

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        rootX = self.find(x)
        rootY = self.find(y)
        if rootX != rootY:
            if self.rank[rootX] > self.rank[rootY]:
                self.parent[rootY] = rootX
            elif self.rank[rootX] < self.rank[rootY]:
                self.parent[rootX] = rootY
            else:
                self.parent[rootY] = rootX
                self.rank[rootX] += 1

def calculate_speed(x, y, uf, gears):
    def get_rotation_speed(x, visited):
        visited.add(x)
        if uf.blocked[x]:
            return (0, 1)
        current_gear = gears[x]
        # find the connected gear
        curr_root = uf.find(x)
        rotations = 1
        for i in range(1, gears[curr_root].teeth + 1):
            next_gear = gears[curr_root].teeth_nodes[i]
            if next_gear not in visited:
                speed, den = get_rotation_speed(next_gear, visited)
                if den == 1:
                    rotations = -rotations * gears[curr_root].teeth / gears[next_gear].teeth * speed
                else:
                    rotations = -rotations * gears[curr_root].teeth / gears[next_gear].teeth * speed // den
                if rotations < 0:
                    rotations = -rotations
                    break
        return (rotations * gears[curr_root].teeth, 1)

    return get_rotation_speed(x, set())

class Gear:
    def __init__(self, teeth):
        self.teeth = teeth
        self.teeth_nodes = {}

def process_queries():
    import sys
    input = sys.stdin.read
    data = input().split()

    N = int(data[0])
    M = int(data[1])
    ind = 2
    gears = [Gear(int(data[ind])) for ind in range(2, 2*N + 2)]
    for i in range(1, N + 1):
        ind += gears[i-1].teeth
    gears = [gears[i - 1] for i in range(1, ind + 1)]
    uf = UnionFind(N+1)

    for i in range(ind + 2, ind + 2 + M):
        cmd = int(data[i])
        if cmd == 1:
