            gears[x - 1].teeth = c
            uf.blocked[x - 1] = False
            for connected in gears[x - 1].teeth_nodes:
                uf.blocked[connected] = False
        elif cmd == 2:
            x, y = int(data[i + 1]), int(data[i + 2])
            uf.union(x, y)
            if uf.find(x) == uf.find(y):
                gears[x - 1].teeth_nodes.update(gears[y - 1].teeth_nodes)
                gears[y - 1].teeth_nodes = {}
            gears[min(x, y) - 1].teeth_nodes[max(x, y) - 1] = gears[max(x, y) - 1]
            gears[max(x, y) - 1].teeth_nodes[min(x, y) - 1] = gears[min(x, y) - 1]
        elif cmd == 3:
            x, y, v = int(data[i + 1]), int(data[i + 2]), int(data[i + 3])
            speed, den = calculate_speed(x, y, uf, gears)
            if speed == 0:
                print(0)
            else:
