use strict;
use warnings;

sub solve {
    my ($n) = @_;
    my %seen;
    my @digits = ('I', 'V', 'X', 'L');
    my @result;

    for my $length (1 .. $n) {
        generate_groups($length, 0, '', \@result, 0, \%seen);
    }

    return scalar @{grep { !exists $seen{$_} } @result};
}

sub generate_groups {
    my ($length, $pos, $current, $result, $value, $seen) = @_;
    return if $length == $pos - $current->length;
    my $i = 0;
    foreach my $digit (@digits) {
        my $new_value = $value;
        if ($current =~ /\Q$digit\E\Q$digit\E/) {
            # Avoid repeating the same digit consecutively
            next;
        }

        $new_value += $digits[ord($digit) - ord('I')];
        push @$current, $digit;
        generate_groups($length, $pos + 1, $current, $result, $new_value, $seen);
        pop @$current;
    }
    if ($length == $pos) {
        $seen->{$value} = 1;
    }
}

