class Solution:
    def tilingRectangle(self, n: int, m: int) -> int:
        max_side = max(n, m)
        min_side = min(n, m)

        squares = 0
        while max_side >= min_side:
            if (n % min_side == 0) and (m % min_side == 0):
                squares += (n // min_side) * (m // min_side)
                n = min_side
                m = min_side
            else:
