import sys

mod = 1000000007

def solution(N, M, K, a):
    jadeja = 0
    dhoni_jars = {}
    for i in range(N):
        jar = a[i]
        if jar not in dhoni_jars:
            dhoni_jars[jar] = 0
        dhoni_jars[jar] += 1

    while dhoni_jars:
        max_jar = max(dhoni_jars, key=dhoni_jars.get)
        if max_jar <= M and dhoni_jars[max_jar] > 0:
            if max_jar + K > 10 ** 9:
                if max_jar + K - 10 ** 9 > M:
                    dhoni = (max_jar + K) % mod
                else:
                    dhoni = M

                remaining = max_jar - dhoni
                jadeja = (jadeja + remaining) % mod
                dhoni_jars[max_jar] -= 1
                if dhoni_jars[max_jar] == 0:
                    del dhoni_jars[max_jar]

                max_jar = max(dhoni_jars, key=dhoni_jars.get)
                if max_jar <= M and dhoni_jars[max_jar] > 0:
                    Dhoni_drank = min(K, max_jar)
                    jadeja = (jadeja + (max_jar - Dhoni_drank)) % mod
                    dhoni_jars[max_jar] -= 1
                    if dhoni_jars[max_jar] == 0:
                        del dhoni_jars[max_jar]
            else:
                jadeja = (jadeja + max_jar) % mod
                del dhoni_jars[max_jar]

    return jadeja

