def play_if_enough(hand, play):
    resources = {'a': 0, 'b': 0, 'c': 0, ..., 'z': 0}
    for resource in hand:
        resources[resource] += 1

    needed = {resource: count for resource, count in zip(play, repeat(1))}
    if needed > resources:
        return [False, '']

    used = set()
    for resource, count in needed.items():
