class ThroneInheritance:
    def __init__(self, kingName: str):
        self.kingName = kingName
        self.king = (self.kingName, None)
        self.alive_persons = {self.kingName: self.king}
        self.dead_persons = set()
        self.order = [self.kingName]

    def birth(self, parentName: str, childName: str) -> None:
        if parentName not in self.alive_persons:
            raise ValueError("Parent must be alive")
        if childName in self.alive_persons or childName in self.dead_persons:
            raise ValueError("Child already exists")
        self.alive_persons[childName] = (None, parentName)
        self.order.append(childName)

    def death(self, name: str) -> None:
        if name not in self.alive_persons:
            raise ValueError("Name not found")
        self.dead_persons.add(name)
        parent, _ = self.alive_persons[name]
        if self.order[-1] == name:
            self.order.pop()
        while self.order and parent:
            self.get_next_in_order(parent)
        parent = self.alive_persons[parent]

    def get_next_in_order(self, person):
        if not person[0] and person[1]:
            self.order.append(self.alive_persons[person[1]][0])
        elif not person[0] and person[1] not in self.alive_persons:
            self.order.pop()
        else:
            self.order.append(Successor(person, self.order))

    def getInheritanceOrder(self) -> List[str]:
        result = []
        for name, data in self.alive_persons.items():
            if name not in self.dead_persons:
                result.append(name)
        return result

def Successor(person, curOrder):
    if person[0] and person[0] in curOrder:
        parent = Successor(person[1], curOrder)
        if parent:
            return parent
        return None
    else:
        curOrder.append(person[0])
        for key, val in list(person[1]):
            if key[0] and key[0] not in curOrder:
                return Successor(key, curOrder)
        curOrder.pop()
        return None
