class Solution:
    def findPermutation(self, s):
        if len(s) == 1:
            return [s]
        result = []
        for i in range(len(s)):
            firstChar = s[i]
            restChars = s[0:i] + s[i+1:]
            for permutation in self.findPermutation(restChars):
                result.append(firstChar + permutation)
        return result

    def lexicographicallyGreater(self, s1, s2):
        for i in range(len(s1)):
            if s1[i] > s2[i]:
                return True
            if s1[i] < s2[i] and s2[i+1:] > s1[i+1:]:
                return True
        return False

    def checkPermutationBreak(self, perm1, perm2):
        return self.lexicographicallyGreater(sorted(perm1), sorted(perm2))

    def checkIfCanBreak(self, s1: str, s2: str) -> bool:
        n = len(s1)
        if n != len(s2):
            return False
        permutations1 = self.findPermutation(s1)
        permutations2 = self.findPermutation(s2)
        for perm1 in permutations1:
            for perm2 in permutations2:
                if self.checkPermutationBreak(perm1, perm2):
                    return True
        for perm1 in permutations2:
            for perm2 in permutations1:
                if self.checkPermutationBreak(perm1, perm2):
                    return True
        return False

