import itertools

class Solution:
    def checkIfCanBreak(self, s1: str, s2: str) -> bool:
        freq1 = {c: s1.count(c) for c in set(s1)}
        freq2 = {c: s2.count(c) for c in set(s2)}

        for perm1 in itertools.permutations(s1):
            for perm2 in itertools.permutations(s2):
                if all(freq1[c] >= freq2[c] for c in zip(perm1, perm2)):
                    return True
