import sys
from collections import defaultdict

class Node:
    def __init__(self, key, val):
        self.key = key
        self.val = val
        self.left = None
        self.right = None

class BBST:
    def __init__(self):
        self.root = None

    def put(self, key, val):
        self.root = self._put(self.root, key, val)

    def _put(self, node, key, val):
        if not node:
            return Node(key, val)

        if key < node.key:
            node.left = self._put(node.left, key, val)
        else:
            node.right = self._put(node.right, key, val)

        node.key = self._rank(node.left) + 1
        return node

    def _rank(self, node):
        if not node:
            return 0
        return self._rank(node.left) + 1 + self._size(node.right)

    def _size(self, node):
        if not node:
            return 0
        return 1 + self._size(node.left) + self._size(node.right)

    def floor(self, key):
        return self._floor(self.root, float('inf')).val

    def _floor(self, node, key):
        if not node:
            return node
        if key < node.key:
            return self._floor(node.left, key)
        if not node.right:
            return node
        return self._floor(node.right, key)

class Solution:
    def getKth(self, lo: int, hi: int, k: int) -> int:
        power_map = defaultdict(list)

        def power(n):
            if n == 1:
                return 0
            power_map[power(n//2) + 1].append(n)
            return 1 + power(3*n+1)

        for n in range(lo, hi+1):
            power_map[power(n)].append((n, power(n)))

        bst = BBST()
        for n, power_val in sorted(power_map.items(), key=lambda x:x[0]):
            bst.put(power_val, n)

        idx = 1
        current_val = bst.floor(k)
        while current_val > bst.root.key:
            bst.root = bst.root.left
            idx += bst.floor(k) - bst._rank(bst.root.left)
        return current_val[1] if idx == k else sys.maxsize
```

