import math

def primes_to_n(n):
    primes = []
    i = 2
    while len(primes) < n:
        if all(i % k != 0 for k in primes):
            primes.append(i)
        i += 1
    return primes

def factor_sum(n, primes=None):
    if not primes:
        primes = primes_to_n(math.isqrt(n) + 1)

    factors = []
    for prime in primes:
        if n % prime == 0:
            power = 0
            while n % prime == 0:
                n //= prime
                power += 1
            factors.append(prime ** power)

    if n > 1:
        factors.append(n)

    if len(factors) > 1:
        return sum(factor_sum(factor) for factor in factors)
    else:
        return factors[0]

