#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

#define INF INT_MAX

using namespace std;

int solve(int n, vector<int> h, vector<int> p) {
    vector<int> visited(n + 1, 0);
    vector<int> left(n + 1, INF);
    vector<int> right(n + 1, INF);

    auto cmp = [&](int a, int b) {
        return left[a] < left[b];
    };

    for (int i = 0; i < p.size(); ++i) {
        int head = lower_bound(h.begin(), h.end(), p[i], cmp) - h.begin();
        visited[head] = 1;
        for (int j = 0; j < head; ++j) {
            if (!visited[j])
                left[j] = min(left[j], abs(p[i] - h[j]));
        }
    }

    for (int i = p.size() - 1; i >= 0; --i) {
        int head = lower_bound(h.begin(), h.end(), p[i], cmp) - h.begin();
        if (visited[head]) {
            for (int j = head + 1; j < n; ++j) {
                if (!visited[j])
                    right[j] = min(right[j], abs(p[i] - h[j]));
            }
        }
    }

    int ans = accumulate(left.begin(), left.end(), 0) - accumulate(right.begin(), right.end(), 0);
    for (int i = 1; i <= n; ++i)
        ans = max(ans, abs(left[i] - i));
    return ans;
}

