```python
class Solution:
    def invalidTransactions(self, transactions: List[str]) -> List[str]:
        invalid_transactions = []
        transactions_dict = {}
        time_threshold = 60

        for transaction in transactions:
            name, time, amount, city = transaction.split(',')

            if int(amount) > 1000:
                invalid_transactions.append(transaction)
                continue

            if city not in transactions_dict:
                transactions_dict[city] = []

            transactions_city = transactions_dict[city]
            previous_transaction = transactions_city[-1] if transactions_city else None

            if previous_transaction and (int(time) - int(previous_transaction.split(',')[1]) <= time_threshold and previous_transaction.split(',')[0] == name and previous_transaction.split(',')[2] != amount):
                invalid_transactions.append(transaction)

        return invalid_transactions
