package main

import (
	"fmt"
	"strings"
	"unicode"
)

func solve(st string) string {
	if len(st) == 0 {
		return ""
	}

	chars := make(map[rune]struct{ first, last int })
	for i, c := range st {
		if _, ok := chars[c]; !ok {
			chars[c] = struct { first, last int }{i, i}
			continue
		}
		chars[c].last = i
	}

	max_val, result := -1, 0
	for _, v := range chars {
		val := v.last - v.first
		if val > max_val {
			max_val = val
			result = v.first
		} else if val == max_val {
			result = unicode.Minuscule(rune(characterCode(v.first) - 1))
		}
	}
	return string(unicode.Rune(result))
}

func characterCode(ch rune) int {
	if ch < 'a' {
		return int(ch)
	}
	return int(ch - 'a' + 1)
}

func main() {
	tests := []string{
		"a",
		"aa",
		"bcd",
		"axyzxyz",
		"dcbadcba",
		"aabccc",
		"efgefg",
		"efghijefghi",
		"acefacef",
		"acefacefacef",
		"",
	}
	for _, test := range tests {
		fmt.Println("solve(", test, ") =", solve(test))
	}
}
