import sys

def dijsktra(s):
    n = len(s)
    dist = [n for _ in range(n)]
    dist[0] = 0
    queue = []
    for i in range(n):
        if s[i] == '1':
            dist[i] = 1
            queue.append((i, 1))

    while queue:
        u, d = queue.pop(0)
        for i in range(u+1, min(u+d+1, n)):
            if s[i] == '0' and dist[i] > dist[u]+1:
                dist[i] = dist[u]+1
                queue.append((i, dist[i]))

    return max(dist)

