```java
import java.util.*;
import java.io.*;

public class Main {
    private final static int MOD = (int) 1e9 + 7;

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int t = sc.nextInt(); // Number of test cases

        for (int q = 1; q <= t; q++) {
            int n = sc.nextInt(); // Number of digits
            int w = sc.nextInt(); // Required weight

            int[] dp = new int[n + 1]; // To store the count of numbers with i digits
            int[] digits = new int[n]; // To store digits in descending order

            dp[0] = 1; // Initializing the count of 0-digit numbers

            // Build the list of digits from 1 to 9 by reusing the dp array
            for (int i = 1; i <= 9; i++) {
                dp[i] = dp[i - 1] * 10 % MOD;
            }

            for (int i = 9; i >= 1; i--) { // Build the list of digits in descending order
                digits[i - 1] = i;
                for (int j = i - 1; j >= 1; j--) {
                    dp[j] = (dp[j] + dp[j - 1]) % MOD;
                    dp[j] -= (dp[Math.min(n, j + 1)] / dp[j - 1]) * (i - digits[j - 1]);
                    dp[j] = (dp[j] % MOD + MOD) % MOD; // handle negative values
                }
            }

            HashMap<Integer, Integer> counts = new HashMap<>();
            for (int i = 1; i <= 9; i++) {
                int weight = i;
                int count = dp[n];
                for (int j = n - 1; j > 0; j--) {
                    weight += digits[j] - digits[j - 1];
                    if (weight > w) break; // invalid weight
                    count -= dp[j - 1];
                    count = (count % MOD + MOD) % MOD; // handle negative values
                    int countKey = ((weight - w + MOD) * power(10, n - j) % MOD + MOD) % MOD;
                    counts.put(countKey, counts.getOrDefault(countKey, 0) + count);
                }
            }
            out.println(counts.getOrDefault(0, 0));
        }

        out.close();
    }

    // Power function
    private static long power(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) > 0) {
                res = (res * a) % MOD;
            }
            a = (a * a) % MOD;
            b = b >> 1;
        }
        return res % MOD;
    }

    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        public FastReader(String file) throws FileNotFoundException {
