```java
import java.io.*;
import java.util.*;

public class RootedTrees {
    static void buildTrees(int[] a, int k, int parent[], int root, List<int[]> trees) {
        if (k == a.length - 1) {
            trees.add(buildArr(a, parent, root));
            return;
        }

        // Go through all possible numbers of vertices for the sub-tree
        for (int v = a[k + 1]; v <= 2 * countSubsum(a, k + 1); v++) {
            int used = 0;
            // Go through all trees for the sub-tree
            for (int[] subTree : buildTrees(a, k + 1, new int[v + 1], 0, new ArrayList<>())) {
                // If there are already v vertices used in the current tree, skip this one
                if (used + countSubsum(subTree, 1) > v) continue;

                // Update parent array for the current sub-tree
                for (int i = 1; i < subTree.length; i++) {
                    if (subTree[i] != 0 && subTree[i] < i) {
                        parent[subTree[i]] = i;
                    }
                }

                // Attach the sub_tree to the current root and update the parent array for the newly created vertices
                for (int i = 0; i < v; i++) {
                    int vertex = a[k + 1] + used + i;
                    parent[vertex] = root;
                }

                used += v;

                // If any of the generated trees are isomorphic, remove one of them
                for (int j = 0; j < trees.size() - 1; j++) {
                    if (isIsomorphic(trees.get(j), subTree)) {
                        trees.remove(j);
                        break;
                    }
                }

                // Add the new tree to the list
                trees.add( buildArr(a, parent, root) );
            }
        }

        return;
    }

    static boolean isIsomorphic(int[] a, int[] b) {
        int[] depth = new int[a.length];
        int[] order = new int[a.length];
        int[] orderB = new int[b.length];

        dfs(a, 1, 0, 0, depth, order);
        dfs(b, 1, 0, 0, depth, orderB);

        for (int i = 0; i < a.length; i++) {
            if (depth[i] != depth[order[i]]) return false;
            if (order[i] != orderB[i]) return false;
        }

        return true;
    }

    static int[] buildArr(int[] a, int[] parent, int root) {
        int[] arr = new int[a.length + 1];
        Arrays.fill(arr, 0);
        arr[0] = 0;
        arr[root] = -1;

        for (int i = 1; i < a.length; i++) {
            int vertex = a[i - 1] + i - 1;
            arr[vertex] = parent[vertex];
        }

        return arr;
    }

    static int countSubsum(int[] a, int k) {
        int sum = 0;
        for (int i = k; i < a.length; i++) sum += a[i];
        return sum;
    }

    static void dfs(int[] arr, int root, int d, int o, int[] depth, int[] order) {
        depth[arr[root]] = d;
        order[o] = arr[root];
        for (int i = arr[root]; i != -1; i = arr[i]) {
            dfs(arr, i, d + 1, o + 1, depth, order);
        }
    }

    public static void main(String[] args) throws IOException {
        Scanner in = new Scanner(System.in);
        int h = in.nextInt();
        int[] a = new int[h + 1];
        for (int i = 0; i <= h; i++) a[i] = in.nextInt();

        int[] parent = new int[sum(a)];
        List<int[]> trees = new ArrayList<>();
        buildTrees(a, 0, parent, 1, trees);

        if (trees.size() == 1) {
