```python
import functools
from typing import List

class NumArray:
    def __init__(self, nums: List[int]):
        n = len(nums)
        self.size = n * 2 - 1
        self.arr = [0] * (self.size + 1)

        def build(x: int, l: int, r: int):
            if l == r:
                self.arr[x] = nums[l]
                return nums[l]

            mid = (l + r) // 2
            self.arr[x] = build(x * 2, l, mid) + build(x * 2 + 1, mid + 1, r)
            return self.arr[x]

        build(1, 0, n - 1)

    def update(self, i: int, val: int) -> None:
        self._update(1, 0, self.size, i, val)

    def _update(self, x: int, l: int, r: int, i: int, val: int) -> None:
        if i < l or i > r:
            return

        if l == r:
            self.arr[x] = val
            return

        mid = (l + r) // 2
        self._update(x * 2, l, mid, i, val)
        self._update(x * 2 + 1, mid + 1, r, i, val)
        self.arr[x] = self.arr[x * 2] + self.arr[x * 2 + 1]

    def sumRange(self, i: int, j: int) -> int:
        return self._sumRange(1, 0, self.size, i, j)

    def _sumRange(self, x: int, l: int, r: int, i: int, j: int) -> int:
        if i > r or j < l:
            return 0

        if i <= l and r <= j:
            return self.arr[x]

        mid = (l + r) // 2
        return self._sumRange(x * 2, l, mid, i, j) + self._sumRange(x * 2 + 1, mid + 1, r, i, j)

# Example usage:
nums = [1, 3, 5]
obj = NumArray(nums)
print(obj.sumRange(0, 2)) # Output: 9
obj.update(1, 2)
print(obj.sumRange(0, 2)) # Output: 8
```

