class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

class Solution:
    def findFrequentTreeSum(self, root: TreeNode) -> List[int]:
        self.result = []
        self.counter = Counter()

        def dfs(node):
            if not node:
                return 0

            sum_ = node.val + dfs(node.left) + dfs(node.right)
