import random

class RandomizedSet:
    def __init__(self):
        self.set = {}
        self.values = []

    def insert(self, val: int) -> bool:
        if val in self.set:
            return False

        self.set[val] = len(self.values)
        self.values.append(val)
        return True

    def remove(self, val: int) -> bool:
        if val not in self.set:
            return False

        last_index = len(self.values) - 1
        last_val = self.values[-1]
        self.values[self.set[val]] = last_val
        self.set[last_val] = self.set[val]
        del self.set[val]
        self.values.pop()

        return True

    def getRandom(self) -> int:
        return random.choice(self.values)
```

