```python
import math

def ext_gcd(a, b):
    if a == 0:
        x = 0
        y = 1
        g = b
    else:
        x, quad_y = ext_gcd(b % a, a)
        y -= quad_y * (a//b)
    return x, y, g

def mod_inverse(a, m):
    g, x, y = ext_gcd(a, m)
    if g != 1:
        return None
    else:
        x %= m
        if x < 0:
            x += m
        return x

def find_multiples(a):
    multiples, g = [], max(a)
    while True:
        g -= 1
        for i in a:
            if i % g != 0:
                break
        else:
            multiples.append(g)
        if g == 1:
            break
    return multiples

def mod_eq_values(lst):
    g = reduce(lambda a, b: gcd(a, b), lst)
    lcm = reduce(lambda a, b: lcm(a, b), lst)

    mults_g = find_multiples([g])
    mults_lcm = [i * math.sqrt(lcm) for i in range(1, math.floor(math.sqrt(lcm)) + 1)]
    multiples = [g] + mults_g + mults_lcm

    result = set()
    for k in multiples:
        flag = True
        for i in lst:
            if i % k != lst[0] % k:
                flag = False
                break
        if flag:
            result.add(k)
    return sorted(list(result))

def main():
